#define _YY_GLSLES_ 1
#define Tolerance 0.004 

varying vec2 v_vTexcoord;
varying vec4 v_vColour;

uniform vec3 sand1;
uniform vec3 sand2;
uniform vec3 sand3;
uniform vec3 sand4;
uniform vec3 sandcol;

void main()
{ 
    vec4 col = texture2D(gm_BaseTexture,v_vTexcoord);
    vec3 finalcol = vec3(dot(col.rgb, vec3(0.3, 0.59, 0.11)));
	
	vec3 col255 = floor(col.rgb*255.0);
	
	if (
	(distance(col255.rgb,sand1)<Tolerance) 
	|| (distance(col255.rgb,sand2)<Tolerance) 
	|| (distance(col255.rgb,sand3)<Tolerance) 
	|| (distance(col255.rgb,sand4)<Tolerance) 
	) 
	{
		finalcol = sandcol;
	}

    gl_FragColor = vec4(finalcol, col.a);
}

