#define _YY_GLSLES_ 1
//
// Fake depth for the rotating tower
//
varying vec2 v_vTexcoord;
varying vec4 v_vColour;
uniform vec2 iExtents;
uniform float iCenterX;

void main()
{
	float uvadjust = clamp((v_vTexcoord.x-iCenterX) * 1.05, -iExtents.x, iExtents.y);
	vec2 adjustedUV = vec2(uvadjust + iCenterX, v_vTexcoord.y);
    float ds = texture2D( gm_BaseTexture, adjustedUV).a;
	ds = mix(0.0, ds, 1.0-sign(abs(v_vTexcoord.x-iCenterX)-(iExtents.y)));
	vec4 col2 = v_vColour * texture2D( gm_BaseTexture, v_vTexcoord);
	float dist = abs(adjustedUV.x-v_vTexcoord.x);
    gl_FragColor.rgb = mix(vec3(ds * (0.08-2.0*dist)), col2.rgb, col2.a);
	gl_FragColor.a = mix(0.0, 1.0, max(ds - 2.0 * dist, 0.0) + col2.a);
}

