//
// Fountain effect for the rotating tower
//
#ifdef GL_NV_desktop_lowp_mediump
	precision highp float; 
	precision highp int;
#endif

varying vec2 v_vTexcoord;
varying vec4 v_vColour;

uniform float iTime;
uniform vec2 iPosOffset;
uniform vec2 iResolution;
uniform vec4 uvs;

void main()
{
    vec2 UV = (v_vTexcoord - uvs.xy)/uvs.zw; //remap texture coordinates to 0-1 range.
	vec2 UV1 = vec2(0,0);
	UV1.x = fract(iPosOffset.x + iTime * 0.008 + UV.x);
	UV1.y = fract(iPosOffset.y + iTime * 0.005 + UV.y);
	vec2 UV2 = vec2(0,0);
	UV2.x = fract(iPosOffset.x - iTime * 0.009 + UV.x);
	UV2.y = fract(iPosOffset.y + iTime * 0.007 + UV.y);
	gl_FragColor = vec4(0.0,0.0,0.0,1.0);
	gl_FragColor.rgb = (v_vColour*texture2D(gm_BaseTexture,UV1 * uvs.zw + uvs.xy)).rgb;
	gl_FragColor.rgb += (v_vColour*texture2D(gm_BaseTexture,UV2 * uvs.zw + uvs.xy)).rgb;
	gl_FragColor.rgb *= v_vColour.rgb;
	float falloff = 1.0-abs(UV.x-0.5)*0.75;
	float indent = 0.005;
	gl_FragColor.rgb *= vec3(falloff, falloff, falloff);
	gl_FragColor.a *= sign(UV.x-sin(iTime + UV.y * 6.0)*indent - indent)*2.0;
	gl_FragColor.a *= -sign(UV.x-(1.0-sin(iTime + UV.y * 6.0)*indent - indent));
}

