//
// FRAGMENT: Sprite outline shader
// Adapted from thelucre @ https://github.com/thelucre/gamemaker-scripts/blob/master/shaderOutline.gml
//
varying vec2 v_vTexcoord;
varying vec4 v_vColour;
uniform vec2 i_thickness;
uniform vec4 i_color; 

const vec2 ZERO = vec2(0.0,0.0);
const vec2 ONE = vec2(1.0,1.0);

void main()
{
	vec2 offsetx = vec2(i_thickness.x, 0);
	vec2 offsety = vec2(0, i_thickness.y);
	vec4 newColor = texture2D( gm_BaseTexture, v_vTexcoord.xy );
	float alpha = newColor.a;

	if ( alpha < 0.5 ) { newColor = i_color; }
	alpha = max(alpha, texture2D( gm_BaseTexture, clamp(v_vTexcoord + offsetx, ZERO, ONE)).a);
	alpha = max(alpha, texture2D( gm_BaseTexture, clamp(v_vTexcoord + offsety, ZERO, ONE)).a);
	alpha = max(alpha, texture2D( gm_BaseTexture, clamp(v_vTexcoord - offsetx, ZERO, ONE)).a);
	alpha = max(alpha, texture2D( gm_BaseTexture, clamp(v_vTexcoord - offsety, ZERO, ONE)).a);
	gl_FragColor = newColor;
	gl_FragColor.a = alpha;
}

