#define _YY_GLSLES_ 1
#ifdef GL_NV_desktop_lowp_mediump
	precision highp float; 
	precision highp int;
#endif

//
// Simple passthrough fragment shader
//
varying vec2 v_vTexcoord;
varying vec4 v_vColour;
uniform vec2 distort_pos;
uniform float time;
uniform vec3 donut;

void main()
{
	vec2 p = v_vTexcoord;
	float pi = 3.14159265359;
	
	//Vector from distort pos to frag coord
	vec2 vect = vec2( gl_FragCoord.x - distort_pos.x, gl_FragCoord.y - distort_pos.y);
	
	//vec2 vectN = normalize(vect);
	//Length of vector
	float dist = sqrt(vect.x * vect.x + vect.y * vect.y);
	
	//Angle (0-1, counterclockwise from right) of vector
	float angle = atan(vect.y, -vect.x) / ( pi);
	float distort = sin( (clamp( dist - donut.x, 0.0, donut.y) / donut.y) * pi) * donut.z;
	
	//distort pixels
	p.x = p.x + (distort * cos(angle)) * 0.1;
	p.y = p.y + (distort * cos(angle)) * 0.1;
	
    gl_FragColor = v_vColour * texture2D( gm_BaseTexture, p );
	
	//remove alpha from center
	gl_FragColor.a = gl_FragColor.a * ( dist / (donut.x + (donut.y * 0.5)));
}

